package com.example.one;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.boot.web.servlet.support.SpringBootServletInitializer;/** * @author zhoupeng create on 2021/7/13 */@SpringBootApplicationpublic class OneApplication extends SpringBootServletInitializer {    public static void main(String[] args) {        SpringApplication.run(OneApplication.class, args);    }    @Override    protected SpringApplicationBuilder configure(SpringApplicationBuilder builder) {        return builder.sources(OneApplication.class);    }}