package com.example.one.bean;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.ArrayList;import java.util.Collection;import java.util.List;/** * @author zhoupeng create on 2021/7/17 */public class MyUserBean implements UserDetails {    private Long id;    private String name;    private String address;    private String username;    private String password;    private String roles;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        String[] rolesArr = roles.split(",");        List<SimpleGrantedAuthority> authorityList = new ArrayList<>();        for (String role : rolesArr) {            authorityList.add(new SimpleGrantedAuthority(role));        }        return authorityList;    }    @Override    public String getPassword() {        return password;    }    @Override    public String getUsername() {        return username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }}