package com.example.one.mapper;import com.example.one.bean.MyUserBean;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * @author zhoupeng create on 2021/7/17 */@Mapperpublic interface MyUserMapper {    @Select("select * from user where username = #{username}")    MyUserBean selectByUsername(@Param("username") String username);}