package com.example.one.security;import com.example.one.bean.MyUserBean;import com.example.one.mapper.MyUserMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Component;/** * @author zhoupeng create on 2021/7/17 */@Componentpublic class MyUserDetailService implements UserDetailsService {    @Autowired    private MyUserMapper myUserMapper;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        MyUserBean myUserBean = myUserMapper.selectByUsername(username);        if (myUserBean == null) {            throw new UsernameNotFoundException("用户不存在! ");        }        return myUserBean;    }}