package com.example.one.security.login;import org.springframework.security.web.authentication.WebAuthenticationDetails;import javax.servlet.http.HttpServletRequest;/** * 扩展登录信息（手机验证码），因为UserDetails中只有name和pwd，没有验证码 */public class MyAuthenticationDetails extends WebAuthenticationDetails {    private final String verifyCode;    public MyAuthenticationDetails(HttpServletRequest request) {        super(request);        verifyCode = request.getParameter("verifyCode");    }    public String getVerifyCode() {        return verifyCode;    }}