package com.example.one.security.login;import org.springframework.security.authentication.AuthenticationDetailsSource;import org.springframework.security.web.authentication.WebAuthenticationDetails;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletRequest;/** * @author zhoupeng create on 2021/7/19 */@Componentpublic class MyAuthenticationDetailsSource implements AuthenticationDetailsSource<HttpServletRequest, WebAuthenticationDetails> {    @Override    public WebAuthenticationDetails buildDetails(HttpServletRequest request) {        return new MyAuthenticationDetails(request);    }}