package com.example.one.security.login;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.core.Authentication;import org.springframework.security.core.AuthenticationException;import org.springframework.stereotype.Component;/** * @author zhoupeng create on 2021/7/19 */@Componentpublic class MyAuthenticationManager implements AuthenticationManager {    @Autowired    private MyAuthenticationProvider myAuthenticationProvider;    @Override    public Authentication authenticate(Authentication auth) throws AuthenticationException {        return myAuthenticationProvider.authenticate(auth);    }}